import { Box, Typography, Dialog, TextField, Grid} from "@mui/material";
import DialogActions from '@mui/material/DialogActions';
import DialogContent from '@mui/material/DialogContent';
import DialogTitle from '@mui/material/DialogTitle';
import React, { useState, useRef } from "react";
import theme from "../utils/theme";
import PermMediaIcon from '@mui/icons-material/PermMedia';
import { Label } from "reactstrap";
import Select from 'react-select';
// import { styled } from '@mui/material/styles';
// import singleLayer from "../static/image/singleLayer.png";
// import doubleLayer from "../static/image/doubleLayer.png";


const sizingOptions = [
  { value: 4, label: '4 inches (for 1-2 people, start from $180)' },
  { value: 6, label: '6 inches (for 2-3 people, +$20)' },
  { value: 8, label: '8 inches (for 3-4 people, +$60)' },
  { value: 10, label: '10 inches (for 5-6 people, +$100)' },
  { value: 12, label: '12 inches (for 6-7 people, +$140)' },
  { value: 12, label: '6 inches base + 4 inches top (for 4-5 people, +$60)' },
]
const flavorsOptions = [
  { value: 'vanilla', label: 'permium vanilla' },
  { value: 'peachOolong', label: 'White Peach Oolong' },
  { vlaue: 'thaiMilkTea', label: 'thai milk tea'}
]

// const label = { inputProps: { 'aria-label': 'Checkbox demo' } };

// const Item = styled(Paper)(({ theme }) => ({
//   backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
//   ...theme.typography.body2,
//   padding: theme.spacing(1),
//   textAlign: 'center',
//   color: theme.palette.text.secondary,
// }));


const CustomBake = ()=>{

  const [image, setImage] = useState(null);
  const inputRef = useRef(null);
  const [open, setOpen] = useState(false);
  const handleClickOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setImage(e.target.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleBoxClick = () => {
    if (inputRef.current) {
      inputRef.current.click();
    }
  };

  const [selectedOptions, setSelectedOptions] = useState([]);


  return <Box maxWidth={'lg'} m="1rem" sx={{display: 'flex', flexDirection: 'column', alignItems: 'center'} }>
    <Typography variant="h5" sx={{ color: `${theme.color.primary}`, alignItems: 'center', textAlign: 'center' }} gutterBottom >
      Custom Bake
    </Typography>
    <Box maxWidth='md' sx={{ boxShadow: '6px 3px 14.3px 0 rgba(0, 0, 0, 0.23)', display:'flex', flexDirection: { xs: 'column' }, padding: '1rem' }}
    >
      <Typography variant="h6" textAlign='center'>Select style you want</Typography>
      <Box className="imageControl" display='flex' flexDirection='row'sx={{height:"200px"}}>
          <Box 
            sx={{ 
              border: '1px dashed #ccc',
              height: "100%",
              width: "200px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              borderRadius: "40px",
              cursor: "pointer",
              flexDirection: "column",
              flex: 1
            }} 
            onClick={handleBoxClick}
          >
          <input type="file" accept="image/*" onChange={handleFileChange} ref={inputRef} style={{ display: 'none' }} />
          {
          image ? (
            <img src={image} alt="Thumbnail" height='100%' width='100%'/> 
          ):(<>
            <PermMediaIcon/><Typography variant="h6">Click to upload image</Typography>
            <Typography variant="p" color="lightgrey">support jpeg, jpg, png</Typography>
          </>)
          }
          </Box>
          <Box sx={{ flex: 1, display: 'flex', alignItems: 'center', justifyContent: 'center', flexDirection: 'column'}}>
            <Typography variant="h4">OR</Typography>
            <Typography variant="h6" onClick={handleClickOpen} sx={{ textDecoration: 'underline', cursor: 'pointer'}} >Select template</Typography>
          </Box>
          <Dialog
            open={open}
            onClose={handleClose}
            aria-labelledby="alert-dialog-title"
            aria-describedby="alert-dialog-description"
          >
            <DialogTitle id="alert-dialog-title">
              Selecting from existing cake templates
            </DialogTitle>
            <DialogContent>

            </DialogContent>
            <DialogActions>
              {/* <Button onClick={handleClose}>cancel</Button>
              <Button onClick={handleClose} autoFocus>
                Select
              </Button> */}
            </DialogActions>
          </Dialog>
      </Box>
      {/* <Box className="detailContol">
        <Label>Your can also provide us with some keywords</Label>
        <TextField display="block" variant="outlined" label="keywords" sx={{margin:"1rem 0"}} fullWidth/>
      </Box> */}
      <Typography variant="h6">Sizing</Typography>
      <Select options={sizingOptions} placeholder="Select size"/>
      <Typography variant="h6">Flavors (select up to 2 flavors)</Typography>
      <Select 
        isMulti
        onChange={(o) => {
          setSelectedOptions(o);
          console.log(selectedOptions);
        }}
        value={selectedOptions}
        isOptionDisabled={() => selectedOptions.length >= 2}
        options={flavorsOptions} 
        placeholder="Select flavors"
      />
      <Typography variant="h6">Colors</Typography>
      <Box>
        <Grid container spacing={2}>
          <Grid item xs={2}>
            <Box sx={{height:'30px', width:'30px',borderRadius:'50%',backgroundColor:'rgb(289, 212, 212)'}}></Box>
          </Grid>
          <Grid item xs={2}>
            <Box sx={{height:'30px', width:'30px',borderRadius:'50%',backgroundColor:'rgb(203, 225, 173)'}}></Box>
          </Grid>
          <Grid item xs={2}>
            <Box sx={{height:'30px', width:'30px',borderRadius:'50%',backgroundColor:'rgb(153, 218, 191)'}}></Box>
          </Grid>
          <Grid item xs={2}>
            <Box sx={{height:'30px', width:'30px',borderRadius:'50%',backgroundColor:'rgb(172, 204, 249)'}}></Box>
          </Grid>
          <Grid item xs={2}>
            <Box sx={{height:'30px', width:'30px',borderRadius:'50%',backgroundColor:'rgb(193, 156, 198)'}}></Box>
          </Grid>
          <Grid item xs={2}>
            <Box sx={{height:'30px', width:'30px',borderRadius:'50%',backgroundColor:'rgb(114, 90, 100)'}}></Box>
          </Grid>
          <Grid item xs={2}>
            <Box sx={{height:'30px', width:'30px',borderRadius:'50%',backgroundColor:'rgb(77, 77, 77)'}}></Box>
          </Grid>
          <Grid item xs={2}>
            <Box sx={{height:'30px', width:'30px',borderRadius:'50%',backgroundColor:'rgb(215, 215, 215)'}}></Box>
          </Grid>

        </Grid>
      </Box>
      {/* <Box sx={{display: 'flex'}}>
        <Box flex={1}>
          <img src={singleLayer} height={150} width={150} />
          <Typography variant="p" sx={{display: "block"}}></Typography>
        </Box>
        <Box flex={1}>
          <img src={doubleLayer} height={150} width={150} />
          <Typography variant="p" sx={{display: "block"}}>start from + $60</Typography>
        </Box>
      </Box> */}
    </Box>
  </Box>
}

export default CustomBake;
