import * as React from "react";
import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import Menu from "@mui/material/Menu";
import MenuIcon from "@mui/icons-material/Menu";
import Container from "@mui/material/Container";
import Avatar from "@mui/material/Avatar";
import Tooltip from "@mui/material/Tooltip";
import Divider from "@mui/material/Divider";
import Button from "@mui/material/Button";
import Drawer from "@mui/material/Drawer";
import MenuItem from "@mui/material/MenuItem";
import ShoppingCartIcon from "@mui/icons-material/ShoppingCart";
import Logo from "../static/image/Logo.png";
import Badge from "@mui/material/Badge";
import { useCart } from "../utils/cartContext";
import { useGlobalConfigs } from "../store";
import { Link } from "react-router-dom";
import List from "@mui/material/List";
import ListItem from "@mui/material/ListItem";
import ListItemButton from "@mui/material/ListItemButton";
import ListItemIcon from "@mui/material/ListItemIcon";
import ListItemText from "@mui/material/ListItemText";
import InboxIcon from "@mui/icons-material/MoveToInbox";
import MailIcon from "@mui/icons-material/Mail";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import { useTheme } from "@mui/material/styles";
import config from "../utils/config";

const ResponsiveAppBar = () => {
  const { cartItems } = useCart();
  const [state, dispatch] = useGlobalConfigs();
  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [anchorElUser, setAnchorElUser] = React.useState(null);
  const [drawState, setState] = React.useState({
    top: false,
    left: false,
    bottom: false,
    right: false,
  });
  const theme = useTheme();

  // console.log(cartItems);
  // console.log(state.header);
  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleOpenUserMenu = (event) => {
    setAnchorElUser(event.currentTarget);
  };
  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };
  const handleCloseUserMenu = () => {
    setAnchorElUser(null);
  };
  const toggleDrawer = (anchor, open) => (event) => {
    if (
      event.type === "keydown" &&
      (event.key === "Tab" || event.key === "Shift")
    ) {
      return;
    }

    setState({ ...state, [anchor]: open });
  };

  const list = (anchor) => (
    <Box
      sx={{ width: anchor === "top" || anchor === "bottom" ? "auto" : 250 }}
      role="presentation"
      onClick={toggleDrawer(anchor, false)}
      onKeyDown={toggleDrawer(anchor, false)}
    >
      <List>
        {cartItems.length === 0 ? (
          <Typography component="div" variant="h5">
            cart is empty
          </Typography>
        ) : (
          cartItems.map((element, index) => (
            <ListItem key={index} disablePadding>
              <Card sx={{ display: "flex" }}>
                <Box sx={{ display: "flex", flexDirection: "column" }}>
                  <CardContent sx={{ flex: "1 0 auto" }}>
                    <Typography component="div" variant="h5">
                      {element.product_name}
                    </Typography>
                  </CardContent>
                  <Box
                    sx={{ display: "flex", alignItems: "center", pl: 1, pb: 1 }}
                  >
                    <button>remove</button>
                  </Box>
                </Box>
                <CardMedia
                  component="img"
                  sx={{ width: 151 }}
                  image={element.image_urls}
                  alt="Live from space album cover"
                />
              </Card>
            </ListItem>
          ))
        )}
      </List>
    </Box>
  );

  const totalQuantity = cartItems.reduce(
    (total, item) => total + item.quantity,
    0,
  );

  return (
    <AppBar
      style={{
        backgroundColor: `rgb(168, 138, 114)`,
        boxShadow: "unset",
        color: "black",
      }}
      position="sticky"
    >
      <Container maxWidth="xl" sx={{ padding: { xs: "unset" } }}>
        <Toolbar disableGutters>
          {/* 主logo */}
          <Link to="/">
            <img src={Logo} alt="logo" width={120} />
          </Link>
          {/* 主title */}
          <Typography
            variant="h6"
            noWrap
            component="a"
            href={"/"}
            sx={{
              mr: 2,
              display: { xs: "none", md: "flex" },
              fontFamily: "monospace",
              fontWeight: 700,
              letterSpacing: ".3rem",
              color: "inherit",
              textDecoration: "none",
            }}
          >
            elf cake
          </Typography>
          {/* 主菜单 */}
          <Box sx={{ flexGrow: 1, display: { xs: "none", md: "flex" } }}>
            {state.header &&
              state.header.navBar &&
              state.header.navBar.map((element) => (
                <Link to={element.link} key={element.title}>
                  <Button
                    key={element.title}
                    sx={{
                      my: 2,
                      color: "white",
                      display: "block",
                      color: "white",
                    }}
                  >
                    {element.value}
                  </Button>
                </Link>
              ))}
          </Box>
          {/* 购物车icon */}
          <Box
            sx={{
              flexGrow: 1,
              display: { xs: "flex" },
              flexDirection: { xs: "row-reverse" },
            }}
          >
            <div>
              <IconButton onClick={toggleDrawer("right", true)}>
                <Badge badgeContent={totalQuantity} color="primary">
                  <ShoppingCartIcon color="action" />
                </Badge>
              </IconButton>
              <Drawer
                anchor={"right"}
                open={drawState["right"]}
                onClose={toggleDrawer("right", false)}
              >
                {list("right")}
              </Drawer>
            </div>
          </Box>
          {/* 头像 */}
          {/* <Box sx={{ flexGrow: 1, display: { xs: 'flex' }, flexDirection: { xs: 'row-reverse' } }} >
            <Tooltip title="Open settings">
              <IconButton onClick={handleOpenUserMenu} sx={{ p: 0 }}>
                <Avatar alt="Remy Sharp" src="/static/images/avatar/2.jpg" />
              </IconButton>
            </Tooltip>
            <Menu
              sx={{ mt: '45px' }}
              id="menu-appbar"
              anchorEl={anchorElUser}
              anchorOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              keepMounted
              transformOrigin={{
                vertical: 'top',
                horizontal: 'right',
              }}
              open={Boolean(anchorElUser)}
              onClose={handleCloseUserMenu}
            >
              {settings.map((setting) => (
                <MenuItem key={setting} onClick={handleCloseUserMenu}>
                  <Typography textAlign="center">{setting}</Typography>
                </MenuItem>
              ))}
            </Menu>
          </Box> */}
          {/* responsve 菜单按钮 */}
          <Box
            sx={{
              flexGrow: { xs: 0, md: 1 },
              display: { xs: "flex", md: "none" },
              flexDirection: { xs: "row-reverse" },
            }}
          >
            <IconButton
              size="large"
              aria-label="account of current user"
              aria-controls="menu-appbar"
              aria-haspopup="true"
              onClick={handleOpenNavMenu}
              color="inherit"
            >
              <MenuIcon />
            </IconButton>
            <Menu
              id="menu-appbar"
              anchorEl={anchorElNav}
              anchorOrigin={{
                vertical: "bottom",
                horizontal: "left",
              }}
              keepMounted
              transformOrigin={{
                vertical: "top",
                horizontal: "left",
              }}
              open={Boolean(anchorElNav)}
              onClose={handleCloseNavMenu}
              sx={{
                display: { xs: "block", md: "none" },
              }}
            >
              {state.header &&
                state.header.navBar &&
                state.header.navBar.map((element) => (
                  <Link to={element.link} key={element.title}>
                    <MenuItem key={element.title} onClick={handleCloseNavMenu}>
                      <Typography style={{ color: "black" }} textAlign="center">
                        {element.value}
                      </Typography>
                    </MenuItem>
                  </Link>
                ))}
            </Menu>
          </Box>
        </Toolbar>
      </Container>
    </AppBar>
  );
};
export default ResponsiveAppBar;
