import React, { useEffect, useRef } from "react";
import AllComponents from "../components";
import { useGlobalConfigs } from "../store";
import { Box } from "@mui/material";
import styled from "styled-components";
import banner from "../static/image/banner.jpeg";
import Typography from "@mui/material/Typography";
import 'swiper/swiper-bundle.min.css';
import 'swiper/swiper.min.css';
import ItemCard from "../components/ItemCard";
import { register } from 'swiper/element/bundle';

const Banner = styled(Box)`
  margin: 0 auto;
  background-image: url(${banner});
  background-repeat: no-repeat;
  background-size: cover;
  height: 100vh;
`;

const HomePage = () => {
  const [state] = useGlobalConfigs();
  const swiperRef = useRef(null);
  useEffect(() => {
    console.log(state.product);
    register();
    const params = {
      slidesPerView: 3,
      breakpoints: {
        768: {
          slidesPerView: 4,
        },
      },
    };
    Object.assign(swiperRef.current, params);

    swiperRef.current.initialize();
  })

  return (
    <>
      <Banner>
        <Box
          sx={{
            display: "flex",
            width: "50%",
            height: "75%",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            gap: "1rem",
          }}
        >
          <Typography variant="h3" sx={{ color: "white" }} gutterBottom>
            HAND CRAFTED
          </Typography>
          <Typography variant="h4" sx={{ color: "white" }} gutterBottom>
            Premium Ingredients
          </Typography>
        </Box>
      </Banner>
      <Box sx={{ maxWidth: 'md' }} style={{ margin: '1rem auto' }}>
        <Typography align="left" varian="h2">
          MOST POPULAR - Made traditionally for modern tastes
        </Typography>
        {/* <Swiper
          spaceBetween={10}
          slidesPerView={3}
          pagination={true}
          modules={[Pagination]}
        >
          {
            (state.product !== null) && state.product.map((element) => {
              return (<SwiperSlide>
                <ItemCard data={element} />
              </SwiperSlide>)
            })
          }
        </Swiper> */}

      </Box>
      <Box> placeholder footer </Box>
    </>
  );
};

export { HomePage };
