import { Box, Typography } from "@mui/material";
import React, { useState, useRef } from "react";
import theme from "../utils/theme";

const CustomBake = ()=>{

  const [image, setImage] = useState(null);
  const inputRef = useRef(null);

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onload = (e) => {
        setImage(e.target.result);
      };

      reader.readAsDataURL(file);
    }
  };

  const handleUploadClick = () => {
    if (inputRef.current) {
      inputRef.current.click();
    }
  };

  const handleBoxClick = () => {
    if (inputRef.current) {
      inputRef.current.click();
    }
  };

  return <Box maxWidth={'lg'}>
    customBake page placeholder
    <Typography variant="h5" sx={{ color: `${theme.color.primary}`, alignItems: 'center', textAlign: 'center'}} gutterBottom>
      Custom Bake
    </Typography>
    <Box
      sx={{
        boxShadow: '6px 3px 14.3px 0 rgba(0, 0, 0, 0.23)',
        display:'flex',
        flexDirection: { xs: 'column', sm: 'md'}
      }}
    >
      <Box flex={1}>
        {/* <Typography variant="p">Upload your own images</Typography> */}
        <Box height={200} width={200} sx={{ border: '1px solid black'}} onClick={handleBoxClick}>
        {
        image ? (
          <img src={image} alt="Thumbnail" height='100%' width='100%'/> 
        ):(<Typography variant="p">Click to uploadimages</Typography>)
        }
        </Box>
        <Box>
          <input type="file" accept="image/*" onChange={handleFileChange} ref={inputRef} style={{ display: 'none' }} />
        </Box>
      </Box>
      <Box flex={1}></Box>
      <Box flex={1}></Box>
    </Box>
  </Box>
}

export default CustomBake;
