import React, { useEffect } from "react";
import AllComponents from "../components";
import { useGlobalConfigs } from "../store";
import { Box } from "@mui/material";
import styled from "styled-components";
import banner from "../static/image/banner.jpeg";
import Typography from "@mui/material/Typography";
import { Swiper, SwiperSlide } from "swiper/react";
import 'swiper/swiper-bundle.min.css';
import 'swiper/swiper.min.css';
import ItemCard from "../components/ItemCard";

const Banner = styled(Box)`
  margin: 0 auto;
  background-image: url(${banner});
  background-repeat: no-repeat;
  background-size: cover;
  // width: 100vw;
  height: 100vh;
`;

const HomePage = () => {
  const [state] = useGlobalConfigs();
  console.log(state.product);
  return (
    <>
      <Banner>
        <Box
          sx={{
            display: "flex",
            width: "50%",
            height: "75%",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            gap: "1rem",
          }}
        >
          <Typography variant="h3" sx={{ color: "white" }} gutterBottom>
            HAND CRAFTED
          </Typography>
          <Typography variant="h4" sx={{ color: "white" }} gutterBottom>
            Premium Ingredients
          </Typography>
        </Box>
      </Banner>
      <Box>
        <Swiper
          spaceBetween={50}
          slidesPerView={3}
          centeredSlides
          onSlideChange={() => console.log("slide change")}
          onSwiper={swiper => console.log(swiper)}
        >
          {
            (state.product !== null) && state.product.map((element, index) => {
              return (<SwiperSlide>
                <ItemCard data={element} />
              </SwiperSlide>)
            })
          }
        </Swiper>
      </Box>
    </>
  );
};

export { HomePage };
