import axios from "axios";

const api_getOne = (data) => {
  const itemData = {}
  axios
    .get(`http://localhost:8080/api/product/${data}`)
    .then((response) => {
      return itemData.info = response.data;
    })
    .catch((error) => {
      return error;
    });
  return axios
    .get(`http://localhost:8080/api/product/${data}/image`)
    .then((response) => {
      return itemData.imgs = response.data;
    })
    .catch((error) => {
      return error;
    });
  console.log(itemData)
  return itemData;
};

export default api_getOne;
