import React, { useEffect, useRef } from "react";
import { useGlobalConfigs } from "../store";
import styled from "styled-components";
import { Box, Typography } from "@mui/material";
import ItemCard from "../components/ItemCard";
import banner from "../static/image/banner.jpeg";
import { Swiper, SwiperSlide } from "swiper/react";
import SwiperCore, { Pagination, Autoplay } from 'swiper';
import 'swiper/swiper-bundle.min.css';
import 'swiper/swiper.min.css';
import 'swiper/components/pagination/pagination.min.css';

const Banner = styled(Box)`
  margin: 0 auto;
  background-image: url(${banner});
  background-repeat: no-repeat;
  background-size: cover;
  height: 100vh;
`;

const fakeData = {
  product_id: 1,
  product_name: "小天使",
  description: "小天使主题蛋糕",
  price: 500,
  stock: 100,
  category_id: 1,
  image_urls: "/api/product/1/thumbnail/1"
}

const HomePage = () => {
  const [state] = useGlobalConfigs();
  SwiperCore.use([Pagination, Autoplay]);

  useEffect(() => {
    console.log(state.product);
  })

  return (
    <>
      <Banner>
        <Box
          sx={{
            display: "flex",
            width: "50%",
            height: "75%",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            gap: "1rem",
          }}
        >
          <Typography variant="h3" sx={{ color: "white" }} gutterBottom>
            HAND CRAFTED
          </Typography>
          <Typography variant="h4" sx={{ color: "white" }} gutterBottom>
            Premium Ingredients
          </Typography>
        </Box>
      </Banner>
      {/* swpier */}
      <Box sx={{ maxWidth: 'lg' }} style={{ margin: '0 auto' }}>
        <Box sx={{ maxWidth: '1000px' }} style={{ margin: '1rem auto' }}>
          <Typography align="left" variant="h4">
            MOST POPULAR - Made traditionally for modern tastes
          </Typography>
          <Swiper
            style={{ height: "390px", maxWidth: '1000px' }}
            pagination={{
              clickable: true,
            }}
            spaceBetween={10}
            slidesPerView="auto"
            breakpoints={{
              0: {
                slidesPerView: 'auto',
              },
              750: {
                slidesPerView: 4,
              },
            }}
          >
            {
              (state.product !== null) && state.product.map((element, index) => {
                return (<SwiperSlide key={index}>
                  <ItemCard data={element} />
                </SwiperSlide>)
              })
            }
          </Swiper>
        </Box>
      </Box>
      {/* secondary banner */}
      <Box>

      </Box>
      {/* our story */}
      <Box
        sx={{ maxWidth: '1000px', display: 'flex', flexDirection: 'row' }}
        style={{ margin: '1rem auto' }}>
        <Box sx={{ flex: 1 }}>
          <img src="/" alt='img_placeholder' />
        </Box>
        <Box sx={{ flex: 1 }}>
          <Typography variant="h4" align="left">
            Our story
          </Typography>
          <Typography variant="h6" align="left">
            Step into the enchanting world of Dulcet Cakes & Sweets, a delightful boutique patisserie café where your sweetest dreams come to life – Japanese style. Embracing the essence of real ingredients and authentic taste, we take pride in crafting our creations the old-fashioned way, cherishing the beauty nature offers. Each delicacy is infused with love and integrity, placing paramount importance on the exquisite flavors they deliver.
          </Typography>
        </Box>
      </Box>

      <Box> placeholder footer </Box>
    </>
  );
};

export { HomePage };
