import { Box, Typography } from "@mui/material";
import React from "react";
import theme from "../utils/theme";
const CustomBake = ()=>{

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      if (
        file.type === 'image/jpeg' ||
        file.type === 'image/png' ||
        file.type === 'image/jpg'
      ) {
        if (file.size <= 5 * 1024 * 1024) {
          console.log('File is valid:', file);
        } else {
          console.log('File size exceeds 5MB limit.');
        }
      } else {
        console.log('Invalid file type. Please select a jpeg, png, or jpg image.');
      }
    }
  };

  return <Box maxWidth={'lg'}>
    customBake page placeholder
    <Typography variant="h5" sx={{ color: `${theme.color.primary}`, alignItems: 'center', textAlign: 'center'}} gutterBottom>
      Custom Bake
    </Typography>
    <Box
      sx={{
        boxShadow: '6px 3px 14.3px 0 rgba(0, 0, 0, 0.23)',
        display:'flex',
        flexDirection: { xs: 'column', sm: 'md'}
      }}
    >
      <Box flex={1}>
        <Typography variant="p">Upload your own images</Typography>
        <Box>
        <input type="file" accept="image/*" onChange={handleFileChange} />
      </Box>
      {image && (
        <Box mt={2}>
          <Typography variant="p">Thumbnail:</Typography>
          <img src={image} alt="Thumbnail" style={{ maxWidth: '100%', maxHeight: '200px' }} />
        </Box>
      )}
      </Box>
      <Box flex={1}></Box>
      <Box flex={1}></Box>
    </Box>
  </Box>
}

export default CustomBake;
