import React from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { CartProvider } from "./utils/cartContext";
import {
  HomePage,
  LoginPage,
  ItemPage,
  CartPage,
  CheckoutPage,
  ShopPage,
} from "./Pages";
import AllComponents from "./components";
// import Container from "@mui/material/Container";
import Box from "@mui/material/Box";
import styled from "styled-components";
import "./app.css";

const NavBarDiv = styled.div`
  position: sticky;
  top: 0;
  background: white;
`;

const App = () => {
  return (
    <Router>
      <CartProvider>
        <Routes>
          <Route path="/" element={<AppWithNavBar element={<HomePage />} />} />
          <Route path="/login" element={<LoginPage />} />
          <Route
            path="/product/:id"
            element={<AppWithNavBar element={<ItemPage />} />}
          />
          <Route
            path="/cart"
            element={<AppWithNavBar element={<CartPage />} />}
          />
          <Route
            path="/checkout"
            element={<AppWithNavBar element={<CheckoutPage />} />}
          />
          <Route
            path="/shop"
            element={<AppWithNavBar element={<ShopPage />} />}
          />
        </Routes>
      </CartProvider>
    </Router>
  );
};

// eslint-disable-next-line react/prop-types
const AppWithNavBar = ({ element }) => (
  <>
    <AllComponents.TopBar />
    <Box maxWidth="xg">
      <AllComponents.ResponsiveAppBar />
      {element}
    </Box>
  </>
);

// eslint-disable-next-line react/prop-types
const APPWithoutNavBar = ({ element }) => (
  <>
    {
      (element === <HomePage />) ? (<AllComponents backgroundColor='rgb(168, 138, 114)' />) : (<AllComponents.TopBar />)
    }
    <Box maxWidth="xg">
      {element}
    </Box>
  </>
);

export default App;
